import { useState, useEffect } from 'react';
import '../styles/login.css';

function Login() {
  const [state, setState] = useState({
    email: '',
    password: '',
    disabled: true
  });

  function validation() {
    const { email, password, disabled } = state;

    const MAX_LENGTH = 6;
    const re = /\S+@\S+\.\S+/;
    if (re.test(email) && password.length >= MAX_LENGTH) {
      setState({ ...state, disabled: false });
    }
    else {
      setState({ ...state, disabled: true });
    }
  }

  const { email, password } = state;

  useEffect(() => {
    validation();
  }, [email, password]);

  return (
    <main className='login-page'>
      <section>
        <h1>Login</h1>
        <form>
          <div>
            <input
              type='email'
              placeholder='E-mail'
              value={ email }
              onChange={ ({ target }) => {
                setState({ ...state, email: target.value});
              }}
            />
          </div>
          <div>
            <input
              type='password'
              placeholder='Password'
              value={ password }
              onChange={ ({ target }) => {
                setState({ ...state, password: target.value });
              }}
            />
          </div>
          <div>
            <button
              type='submit'
              disabled={ disabled }  
            >
              Login
            </button>
          </div>
        </form>
      </section>
    </main>
  )
}

export default Login;
